--  DDL for Trigger CHECK_CF
--------------------------------------------------------

  CREATE OR REPLACE EDITIONABLE TRIGGER "CHECK_CF" 
BEFORE INSERT ON PERSONA

FOR EACH ROW
BEGIN 
IF :NEW.Cognome IS NOT NULL AND :NEW.Nome IS NOT NULL AND :NEW.DataNascita IS NOT NULL AND :NEW.Sesso IS NOT NULL AND :NEW.ComuneNascita IS NOT NULL THEN
    IF :NEW.CODICEFISCALE IS NOT NULL THEN
        IF Controllo_Completo_CF(:NEW.CodiceFiscale, :NEW.DataNascita, :NEW.Sesso, :NEW.Nome, :NEW.Cognome, :NEW.ComuneNascita) <> 1 THEN
          RAISE_APPLICATION_ERROR( -20111, 'CF INVALIDO');
        END IF;
    ELSE
        :NEW.CodiceFiscale := Genera_CF(:NEW.Cognome, :NEW.Nome, :NEW.DataNascita, :NEW.Sesso, :NEW.ComuneNascita);
    END IF;
END IF;

END;


/
