  CREATE OR REPLACE EDITIONABLE FUNCTION "GET_CIN_CF" (CodiceFiscaleParziale VARCHAR2)
RETURN CHAR IS
codice_pari VARCHAR(8);
codice_dispari VARCHAR(8);
tmp INTEGER := 0;
sommaPari INTEGER := 0;
sommaDispari INTEGER := 0;
sommaTotale INTEGER := 0;
restoLocale INTEGER := 0;
CIN CHAR(1);
    BEGIN
    FOR I IN 1..LENGTH(CodiceFiscaleParziale) LOOP
    IF mod(I,2) = 0 THEN
