  CREATE OR REPLACE EDITIONABLE FUNCTION "SOMMADISPARICF" (CF VARCHAR)
RETURN INTEGER IS 
Somma INTEGER := 0 ;
I INTEGER := 1 ; 
TMP INTEGER := 0 ; 

BEGIN 
	WHILE I < 16 LOOP
		SELECT VALORE INTO TMP 
		FROM Caratteri_alfanumerici_DISPARI
		WHERE Carattere = UPPER(SUBSTR(CF,I,1));
		Somma := Somma + TMP;
		I := I + 2 ; 
	END LOOP;

	RETURN Somma; 

END  SommaDispariCF;



