--  DDL for Function STRINGASOLOCONSONANTI
--------------------------------------------------------

  CREATE OR REPLACE EDITIONABLE FUNCTION "STRINGASOLOCONSONANTI" (stringa VARCHAR2)
	RETURN VARCHAR2 IS
	soluzione VARCHAR2(32) := '';

	BEGIN 
		FOR I IN 1..LENGTH(Stringa) LOOP -- Ciclo per la ricerca delle consonanti
            IF INSTR('aeoiu'' ',LOWER(SUBSTR(Stringa,I,1))) = 0 THEN -- controllo se il mio carattere non è sottostringa di delle vocali
                soluzione := CONCAT(soluzione, SUBSTR(Stringa,I,1));
            END IF;
       END LOOP;
	RETURN UPPER(soluzione);
END StringaSoloConsonanti;



